local Quad = love.graphics.newQuadfunction love.load()    sprite= {}    sprite.player = love.graphics.newImage("img/sprite.png")    sprite.x = 50    sprite.y = 50    direction = "right"    iteration = 1    max = 8    idle = true    timer = 0.1    quads = {}    quads['right'] ={}    quads['left'] = {}    for j=1,8 do        quads['right'][j] = Quad((j-1)*32, 0, 32, 32, 256, 32);        quads['left'][j] = Quad((j-1)*32, 0, 32, 32, 256, 32);        -- quads.left[j]:flip(true, false)    end    --sexy    settings = love.graphics.newImage("img/settings.png")    --get width and height of windows    width = love.graphics.getWidth()    height = love.graphics.getHeight()    --set hp    hp = 5478    hpMax = 6969    hpFont = love.graphics.newFont(16)endfunction love.update(dt)  if idle == false then    timer = timer + dt    if timer > 0.2 then      timer = 0.1      iteration = iteration + 1      if love.keyboard.isDown('right') then        sprite.x = sprite.x + 5      end      if love.keyboard.isDown('left') then        sprite.x = sprite.x - 5      end      if iteration > max then        iteration = 1      end    end  endendfunction love.keypressed(key)  if quads[key] then    direction = key    idle = false  endendfunction love.keyreleased(key)  if quads[key] and direction == key then    idle = true    iteration = 1    direction = "right"  endendfunction love.draw()    love.graphics.draw(sprite.player, quads[direction][iteration],    sprite.x,    sprite.y)    --sexy    love.graphics.setBackgroundColor(255, 255, 255)    --draw hp    love.graphics.setColor(255, 20, 147)    love.graphics.rectangle("fill", 0, 0, width * hp / hpMax, height / 30)    love.graphics.setFont(hpFont)    love.graphics.setColor(255, 255, 255)    love.graphics.print(hp .. "/" .. hpMax, 0, 0)    --draw icon    love.graphics.draw(settings, width - 24, height - 24) --24x24pend