function love.load()    require "battle"    require "character"    require "benchboard"    battle:load()    character:load()    benchboard:load()endfunction love.update(dt)    battle:update(dt)    character:update(dt)endfunction love.keypressed(key)    battle:keyPress(key)endfunction love.draw()    battle:attack(500, 200, "up")    character:draw()    benchboard:draw()end